'use strict';

var excludeParentKeys = require('./exclude-parent-keys.cjs');
var addFontStyles = require('./add-font-styles.cjs');
var expandComposites = require('./expand-composites.cjs');

function parseTokens(tokens, transformOpts, filePath) {
    const excluded = excludeParentKeys.excludeParentKeys(tokens, transformOpts);
    const withFontStyles = addFontStyles.addFontStyles(excluded, transformOpts);
    const expanded = expandComposites.expandComposites(withFontStyles, transformOpts, filePath);
    return expanded;
}

exports.parseTokens = parseTokens;
